package Programs;

public class Nonparametrised {

	static void add(int a,int b)
	{
		System.out.println(a-b);
	}
	static void sub(int a,int b)
	{
		System.out.println(a*b);
	}
	static void mul()
	{
		int a=10;
		int b=20;
		System.out.println(a*b);
	}
	
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
mul();
sub(10,5);
add(100,333);
	}

}
